CREATE OR REPLACE PROCEDURE P_ASP_INSERT_USERSTATUS
(
    v_resultcode        OUT NUMBER,
    v_resulterrinfo     OUT VARCHAR2
)
-----------------------------------------------------
--VerisBilling参数割接使用
-----------------------------------------------------
AS
BEGIN
    v_resultcode := 0;
    v_resulterrinfo := 'P_ASP_INSERT_USERSTATUS 过程执行成功!';

    BEGIN
    
delete from SD.SYS_USER_STATUS_AFFECT_DEF;
delete from SD.SYS_USER_STATUS_DTL;
COMMIT;


COMMIT;
   
      EXCEPTION
      WHEN OTHERS THEN
           ROLLBACK;
           v_resulterrinfo:='P_ASP_INSERT_USERSTATUS err!:'||substr(SQLERRM,1,200);
           v_resultcode:='-1';
           RETURN;
    END;    
    COMMIT;
    
EXCEPTION
  WHEN OTHERS THEN
       v_resulterrinfo:=substr(SQLERRM,1,200);
       v_resultcode:='-1';
END;
/





